angular.module("app",["ngRoute","ngSanitize"]).config(function(n,t){t.html5Mode(!0),n.when("/",{templateUrl:ngThemeViews.partials+"main.html",controller:"Main"}).when("/:id",{templateUrl:ngThemeViews.partials+"content.html",controller:"Content"})}).controller("Main",function(n,t,o){t.get("wp-json/wp/v2/posts/").success(function(t){n.posts=t}),console.log("main is loaded.")}).controller("Content",function(n,t,o){t.get("wp-json/wp/v2/posts/"+o.id).success(function(t){n.post=t})});