angular.module("app",["ngRoute","ngSanitize"]).config(function(t,e){e.html5Mode(!0),t.when("/",{templateUrl:ngThemeViews.partials+"main.html",controller:"Main"}).when("/:id",{templateUrl:ngThemeViews.partials+"content.html",controller:"Content"})}).controller("Main",function(t,e,n){e.get("wp-json/wp/v2/posts/").success(function(e){t.posts=e}),console.log("main is loaded.")}).controller("Content",function(t,e,n){e.get("wp-json/wp/v2/posts/"+n.id).success(function(e){t.post=e})}).directive("searchForm",function(){return{restrict:"E",templateUrl:ngThemeViews.partials+"search-form.html",controller:function(t,e){t.filter={s:""},t.search=function(){t.filter.s.length>=5&&e.get("wp-json/wp/v2/posts/?filter[s]="+t.filter.s).success(function(e){t.posts=e})}}}}).directive("sidebar",function(){return{restrict:"E",templateUrl:ngThemeViews.partials+"sidebar.html"}}).directive("navigation",function(){return{restrict:"EA",templateUrl:ngThemeViews.partials+"navigation.html"}});